	return 8 * time.Hour
}

func lunchBreak() time.Duration {
	return time.Hour
}

func morningBreak() time.Duration {
	return 15 * time.Minute
}

func afternoonBreak() time.Duration {
	return 15 * time.Minute
}

func physicalActivity() time.Duration {
	return 30 * time.Minute
}

func calculateTasks(workTasksPerDay int) {
	availableWorkTime := workdayDuration() - lunchBreak() - afternoonBreak() - morningBreak() - physicalActivity()
	tasksPerHour := availableWorkTime.Hours() / 45 * workTasksPerDay
	fmt.Printf("Maximum %d work tasks per hour\n", tasksPerHour)
}

func main() {
	targetTasks := 8
	calculateTasks(targetTasks)
}
```
