import Color

func mixColors(color1: UIColor, color2: UIColor) -> UIColor {
    let red = Int(color1.value) / (color1.value + color2.value)
    let green = Int(color2.value) / (color1.value + color2.value)
    let blue = Int(color1.value - color2.value) / (color1.value + color2.value)
    return UIColor(red: red, green: green, blue: blue)
}
```

