    private static string BuildBagOfWords(string[] words)
    {
        var bagOfWords = new Dictionary<string, string>();
        foreach (var word in words)
        {
            var doc = new Document(new List<string> { word });
            bagOfWords.Add(word, doc.Sem);
        }
        return bagOfWords.ToString();
    }

    private static BinaryClassifier CreateClassifier(BagOfWords bagOfWords)
    {
