        };

        classifier.AddWords(bagOfWords);
        return classifier;
    }

    private static string DetermineSentiment(BinaryClassifier classifier, string[] words)
    {
        var sentiment = 0;

        foreach (var word in words)
        {
            var wordDoc = classifier.GetWord(word);
            if (wordDoc.Sentiment == "Positive")
            {
                sentiment++;
            }
        }

        return sentiment.ToString();
    }
}
