    sfVector2f cityCenter(400, 300);

    for (int i = 0; i < 10; ++i)
    {
        // Create a rectangle for each building
        sfRectangleShape* building = new sfRectangleShape();
        building->setCoords(cityCenter + sfVector2f(i * 50, 50), sfVector2f(citySize.x / 2, citySize.y / 2));

        // Create a sprite for each building
        sfSprite building;
        building.setTexture(texture);

        // Add the building to the skyline
        skyline.addChild(building);
    }

    // Set up the meteor
    sfVector2f meteorSize(10, 50);
    sfVector2f meteorCenter(400, 300);
    sfVector2f meteorVelocity(10, 50);
    sfVector2f meteorDirection(0, 1);

    for (int i = 0; i < 10; ++i)
    {
        // Move the meteor
        sfVector2f currentPos = meteorCenter + meteorVelocity;
        sfVector2f nextPos = currentPos + meteorDirection;

        // Update the position of the meteor
        if (window.isOpen())
        {
            sfEvent event;
            while (window.pollEvent(event))
            {
                if (event.type == sfEvtClosed)
                {
                    window.close();
                }
            }

            // Move the meteor
            if (event.type == sfEvtTimerElapsed)
            {
                sfClock clock;
                clock.restart();
                sfVector2f delta = (currentPos - nextPos).normalized();
                meteorVelocity = meteorVelocity - delta;

                // Calculate the next position
                nextPos = currentPos + meteorVelocity;
                currentPos = nextPos;

                // Draw the meteor
                if (window.isOpen())
                {
                    window.clear();
                    window.draw(meteorSprite);
                }
            }
        }
    }

    return 0;
