    function calculateHaversineDistance(lat1, lon1, lat2, lon2) {
      var R = 6371; // Earth's radius in kilometers
      var phi1 = Math.asin(Math.sin(lat1) * Math.sin(lat2) + Math.cos(lat1) * Math.cos(lat2) * Math.cos(lon1 - lon2));
      var phi2 = Math.asin(Math.sin(lat2) * Math.sin(lat1) + Math.cos(lat1) * Math.cos(lat2) * Math.cos(lon1 - lon2));
      var delta = lon2 - lon1;
      var a = Math.sin(delta / 2) * Math.sin(delta / 2) * Math.cos(phi1) * Math.cos(phi2);
      var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
      return R * c;
