function replaceNegativeWords($sentence) {
    $negativeWords = ['slow', 'tedious', 'bad', 'terrible', 'boring', 'dull', 'annoying', 'difficult', 'challenging', 'painful'];
    $antonyms = ['fast', 'interesting', 'good', 'excellent', 'exciting', 'engaging', 'amazing', 'easy', 'pleasant', 'enjoyable'];

