public class Main {
    public static void main(String[] args) {
        int quantity = 10;
        int minOrderQuantity = 5;
        double pricePerItem = 100;
        double discount = 0.15;

        double totalCost = calculateTotalCost(quantity, pricePerItem, minOrderQuantity, discount);
        System.out.println("Total cost: $" + totalCost);
        System.out.println("Number of items needed to reach minimum order quantity: " + minOrderQuantity);
    }

    public static double calculateTotalCost(int quantity, double pricePerItem, int minOrderQuantity, double discount) {
        if (quantity >= minOrderQuantity) {
            double totalCost = quantity * pricePerItem * (1 - discount);
            return totalCost;
        } else {
            return "The minimum order quantity has not been reached.";
        }
    }
}
