```cpp
#include <iostream>
#include <vector>
using namespace std;
class KanbanBoard {
public:
   void addTask(string task) {
      tasks.push_back(task);
   }
   void removeTask(string task) {
      tasks.erase(remove_if(tasks.begin(), tasks.end(), [&](const string& task) {
         return task == task;
      }), tasks.end());
   }
   void displayTasks() {
      cout << "Tasks: ";
      for (const string& task : tasks) {
         cout << task << " ";
      }
      cout << endl;
   }
private:
