    <h1>Top European Destinations</h1>
    <div id="destinations"></div>

    <script>
        $(document).ready(function() {
            $.getJSON("https://www.tripadvisor.com/Travel-Reviews/API/locations.json?locationid=Europe", function(data) {
                var destinations = [];
                $.each(data.locations, function(i, location) {
                    var rating = location.rating;
                    var url = location.placeid;
                    var description = location.placeurl;
                    var name = location.name;

                    var html = "<a href='https://www.tripadvisor.com/" + url + "'>" + name + "</a>: " + rating;
                    destinations.push(html);
                });

                destinations.sort(function(a, b) {
                    return b - a;
                });

                var $destinations = $("#destinations");
                $destinations.html(destinations.join("<br>"));
            });
        });
    </script>
