func isPrime(_ number: Int) -> Bool {
    if number <= 1 {
        return false
    }
    let sqrt = Int(sqrt(Double(number)))
    for i in 2..<sqrt {
        if number % i == 0 {
            return false
        }
    }
    return true
}
```

